import React, {useState, useEffect} from "react";
import {
  SkeletonPage,
  TextContainer,
  TextField,
  Button,
  Modal
} from "@shopify/polaris";


function SnippetGen(props) {
const createSnippet = () => {
    `<script src="https://${props.env}.threekit.com/app/js/threekit-player.js"></script>
    <div id="tkplayer">
    {% assign threekit = product.metafields.threekit %}
    {% assign token = 'token' %}
    <script>
            document.getElementById("tkplayer").style.height='500px';
            document.getElementById("tkplayer").style.width='500px';

            window.threekitPlayer({
            authToken: "{{ threekit[token] }}",
            el: document.getElementById("tkplayer"),
            assetId: ${props.assetid},
            showConfigurator: false,
            showAR: ${props.ar}
          })
          .then(async api => {
             window.player = api;
             window.configurator = await api.getConfigurator();
      });
    </script>`
}
const [value, setValue] = useState('');

useEffect(() => {
    setValue(createSnippet());
  }, [setValue])
console.log(() => createSnippet)
  return (
    <div>
      <Modal
        open={props.active}
        onClose={props.handleChange}
        title={props.title}
        primaryAction={{
          content: "Add Instagram",
          onAction: props.handleChange
        }}
      >
        <Modal.Section>
          <TextContainer>
            <TextField multiline value={value} />
          </TextContainer>
        </Modal.Section>
      </Modal>
    </div>
  );
}

export default SnippetGen;
