import {
  Banner,
  Card,
  DisplayText,
  Form,
  FormLayout,
  Frame,
  Layout,
  Page,
  PageActions,
  TextField,
  Toast
} from "@shopify/polaris";
import store from "store-js";
import gql from "graphql-tag";
import { Mutation, Query, useQuery } from "react-apollo";

const UPDATE_PRICE = gql`
  mutation($input: ProductInput!) {
    productUpdate(input: $input) {
      product {
        metafields(first: 100) {
          edges {
            node {
              id
              namespace
              key
              value
            }
          }
        }
      }
    }
  }
`;

const GET_META_ID = gql`
query($id: ID!) {
  product(id: $id) {
    metafield(namespace: "threekit1", key: "assetid") {
      value
      id
    }
  }
}
`;

const getMeta = (proId) => {
  const { loading, error, data } = useQuery(GET_META_ID, {
    variables: {id: proId}
  });

  if (loading) return null;
  if (error) return `Error! ${error}`;
  console.log(data)
  data.product.metafield.id;
}

class EditProduct extends React.Component {
  state = {
    discount: "",
    price: "",
    variantId: "",
    showToast: false,
    metaId: ""
  };

  componentDidMount() {
    this.setState({ discount: this.itemToBeConsumed() });
    console.log("variant id: ", this.state.variantId);
    this.setState({metaId: this.getMeta(this.state.variantId)})

  }
  

  render() {
    const { name, price, discount, variantId } = this.state;



    console.log(this.state)

    return (
      
      <Mutation mutation={UPDATE_PRICE}>
        {(handleSubmit, { error, data }) => {
          const showError = error && (
            <Banner status="critical">{error.message}</Banner>
          );
          const showToast = data && data.productVariantUpdate && (
            <Toast
              content="Sucessfully updated"
              onDismiss={() => this.setState({ showToast: false })}
            />
          );


          return (
            <Frame>
              <Page>
                <Layout>
                  {showToast}
                  <Layout.Section>{showError}</Layout.Section>
                  <Layout.Section>
                    <DisplayText size="large">{name}</DisplayText>
                    <Form>
                      <Card sectioned>
                        <p>{JSON.stringify(getMeta(this.state.variantId))}</p>
                        <FormLayout>
                          <FormLayout.Group>
                            <TextField
                              prefix="$"
                              value={price}
                              disabled
                              label="Original price"
                              type="price"
                            />
                            <TextField
                              id="sendText"
                              prefix="TKID"
                              value={discount}
                              onChange={this.handleChange("discount")}
                              label="Discounted price"
                              type="text"
                            />
                          </FormLayout.Group>
                          <p>This sale price will expire in two weeks</p>
                        </FormLayout>
                      </Card>
                      <PageActions
                        primaryAction={[
                          {
                            content: "Save",
                            onAction: () => {
                              const productVariableInput = {
                                id: "gid://shopify/Product/4713627418669",
                                metafields: [
                                  {
                                    namespace: "threekit1",
                                    key: "assetid",
                                    value: this.state.discount,
                                    valueType: "STRING",
                                    id: this.state.metaId
                                  }
                                ]
                              };
                              handleSubmit({
                                variables: { input: productVariableInput }
                              });
                            }
                          }
                        ]}
                        secondaryActions={[
                          {
                            content: "Remove discount"
                          }
                        ]}
                      />
                    </Form>
                  </Layout.Section>
                </Layout>
              </Page>
            </Frame>
          );
        }}
      </Mutation>
    );
  }

  handleChange = field => {
    return value => this.setState({ [field]: value });
  };

  itemToBeConsumed = () => {
    const item = store.get("item");
    console.log(item);

    const price = item.handle;
    const variantId = item.id;
    const discounter = price;
    this.setState({ price, variantId });
    return discounter;
  };
}

export default EditProduct;
