import { Layout, Page, TextStyle, EmptyState, Button } from "@shopify/polaris";
import { ResourcePicker, TitleBar } from "@shopify/app-bridge-react";
import store from "store-js";
import ResourceListWithProducts from "../components/ResourceList";
import gql from "graphql-tag";
import { Query } from "react-apollo";

/*

What needs to happen:
  - Page Loads
    - Check the Shop's Threekit Metafield
    - If there are products with Threekit Asset IDs, list them
    - If not, load the empty state
    - When products are selected, their Shopify IDs get entered into the Shop's metafield
  - From the main page, display TK ID & if AR is enabled
*/
// This will always get the Shop's Threekit Metafield


// Get the shop's threekit metafield
const GET_TK_METAFIELD = gql`
query {
  shop{
    metafield(namespace: "threekit", key: "items") {
      id,
      value
    }
  }
}
`



class Index extends React.Component {
  state = {
    open: false,
    hasSrc: false,
    shop: "",
    tkMetaId: {},
    metaID: ''
  };

  _queryMe = data => {
    if (data.shop.metafield == null){
      return null;
    } else {
      this.setState({
        tkMetaId: data,
        metaID: data.shop.metafield.id.split('Metafield/')[1]
      })
    }

  }


  _checkIfTK = () => {
    if(this.state.tkMetaId.shop.metafield == null){
      console.log('no TK id')
    } else {
      console.log("Yes this is TK")
    }
  }


  // For testing
  getShopMetafield = () => {
    var fetchUrl = "/api/shopMeta";
    var method = "GET";
    fetch(fetchUrl, { method: method })
      .then(response => response.json())
      .then(json => {
        // console.log(json.data.metafields)
        // this.setState({tkMetaId: json.data})
        return json.data
      });
  };

  updateShopMetafield = (id, val) => {
    var fetchUrl = `/api/insertMeta/${id}/${val}`;
    var method = "GET";
    fetch(fetchUrl, { method: method })
      .then(response => response.json())
      .then(json => {
        console.log("UPDATED")
        console.log(json)
        // this.setState({tkMetaId: json.data})
        return json.data
      });
  };

  makeShopMetafield = (val) => {
    var fetchUrl = "/api/makeMeta/" + val;
    var method = "GET";
    fetch(fetchUrl, { method: method })
      .then(response => response.json())
      .then(json => {
        // console.log(json);
        // console.log(json.data.metafields["threekitItems"])
      });
  };
  

  initializeSrc = () => {
    var fetchUrl = "/make/script_tags";
    var method = "GET";
    fetch(fetchUrl, { method: method })
      .then(response => response.json())
      .then(json => {
        var fetchUrl = "/make/script_tags";
        var method = "GET";
        fetch(fetchUrl, { method: method });
        console.log(json);
      });
  };

  deleteSrc = () => {
    var fetchUrl = "/delete/script_tags/118167273517";
    var method = "GET";
    fetch(fetchUrl, { method: method })
      .then(response => response.json())
      .then(json => {
        console.log(json);
      });
  };

  getScripts = () => {
    var fetchUrl = "/api/script_tags";
    var method = "GET";
    fetch(fetchUrl, { method: method })
      .then(response => response.json())
      .then(json => console.log(json));
  };
  // End testing functions

  componentDidMount() {
    this.getShopMetafield();
    console.log(this.state)
    // this.setState({tkMetaId: this.getShopMetafield()})
    this.makeShopMetafield();
    // this.getScripts();
    // this.initializeSrc();
    // this.deleteSrc();
  }

  render() {
    // This retrieves any locally stored items
    const emptyState = !store.get("ids");

    return (
      <Page>
        <TitleBar
          primaryAction={{
            content: "Select products",
            onAction: () => this.setState({ open: true })
          }}
        />
        <ResourcePicker
          resourceType="Product"
          showVariants={false}
          open={this.state.open}
          onSelection={resources => this.handleSelection(resources)}
          onCancel={() => this.setState({ open: false })}
        />
        {emptyState ? (
          <Layout>
            <EmptyState
              heading="Associate a Shopify product with a Threekit item."
              action={{
                content: "Select products",
                onAction: () => this.setState({ open: true })
              }}
              image={
                "https://cdn.shopify.com/s/files/1/0757/9955/files/empty-state.svg"
              }
            >
              <p>Select products to associate it with a Threekit item.</p>
            </EmptyState>
          </Layout>
        ) : (
          <div>
            <Query query={GET_TK_METAFIELD} onCompleted={data => this._queryMe(data)}>
              {() => {
                return (
                  <div>
                    <p>{JSON.stringify(this.state)}</p>
                    
                    <Button onClick={() => console.log("Clicked")}>Button</Button>
                  </div>
                );
              }}
            </Query>
          <ResourceListWithProducts />

          </div>
        )}
      </Page>
    );
  }
  handleSelection = resources => {
    const idsFromResources = resources.selection.map(product => product.id);
    this.setState({ open: false });
    store.set("ids", idsFromResources);
  };
}
export default Index;
