import React, { useState, useEffect } from "react";
import {
  SkeletonPage,
  TextContainer,
  TextField,
  Button,
  Modal
} from "@shopify/polaris";

function SnippetGen(props) {
  const createSnippet = function() {
    let newTitle = props.title.replace(/\s/g, "");
    return `<div id="tkplayer-${newTitle}" style="height: 500px; width: 500px;">
        <script>
                window.threekitPlayer({
                authToken: "{{ threekit[token] }}",
                el: document.getElementById("tkplayer-${newTitle}"),
                assetId: '${props.assetid}',
                showConfigurator: false,
                showAR: ${props.ar}
              })
              .then(async api => {
                 window.player = api;
                 window.configurator = await api.getConfigurator();
          });
        </script>`;
  };
  const [value, setValue] = useState("");

  useEffect(() => {
    setValue(createSnippet());
  }, [setValue]);
  return (
    <div>
      <Modal
        open={props.active}
        onClose={props.handleChange}
        title={props.title}
        primaryAction={{
          content: "Copy to clipboard",
          onAction: props.handleChange
        }}
      >
        <Modal.Section>
          <TextContainer>
            <p>Use this piece of HTML to embed the selected item in a Threekit player. You can paste this code anywhere HTML input is supported. </p>
          </TextContainer>
        </Modal.Section>
        <Modal.Section>
          <TextContainer>
            <TextField multiline value={value} />
          </TextContainer>
        </Modal.Section>
      </Modal>
    </div>
  );
}

export default SnippetGen;
