import React, { useState, useCallback } from "react";
import { Query } from "react-apollo";
import { TextField, Card, Stack, TextStyle, Checkbox } from "@shopify/polaris";
function ThreekitForm(props) {
  // Declare a new state variable, which we'll call "count"
  const [checked, setChecked] = useState(false);
  const handleChange = useCallback(newChecked => {
    setChecked(newChecked);
    console.log("test");
  }, []);
  return (
    <div>
      <Query
        query={props.query}
        variables={props.id}
        // skip={!breed}
      >
        {({ loading, error, data, refetch }) => {
          if (loading) return null;
          if (error) return `Error! ${error}`;
          return (
            <div>
              {/* <Card title={props.title}> */}
              {console.log(data)}
              {!data.product.metafield ? "" : setChecked(true)}
              <Stack>
                <Stack.Item fill>
                  <h3>
                    <TextStyle variation="strong">{props.title}</TextStyle>
                  </h3>


                </Stack.Item>
              </Stack>
              {/* Catch the error for no IDS */}

                <TextField
                  value={props.field}
                  id={data.product.id}
                  ariaControls={data.product.id}
                  placeholder={
                    !data.product.metafield
                      ? "No Threekit ID"
                      : data.product.metafield.value
                  }
                  onChange={props.handleTKChange}
                ></TextField>


              {/* <button onClick={() => refetch()}>Refetch!</button> */}
              {/* </Card> */}
            </div>
          );
        }}
      </Query>
    </div>
  );
}

export default ThreekitForm;
