import gql from "graphql-tag";
import { Query, ApolloConsumer } from "react-apollo";
import {
  Card,
  ResourceList,
  Stack,
  TextStyle,
  Thumbnail,
  TextField,
  Button,
  Collapsible,
  TextContainer
} from "@shopify/polaris";
import store from "store-js";
import { Redirect } from "@shopify/app-bridge/actions";
import { Context } from "@shopify/app-bridge-react";

const GET_PRODUCTS_BY_ID = gql`
  query getProducts($ids: [ID!]!) {
    nodes(ids: $ids) {
      ... on Product {
        title
        handle
        descriptionHtml
        id
        images(first: 1) {
          edges {
            node {
              originalSrc
              altText
            }
          }
        }
        variants(first: 1) {
          edges {
            node {
              price
              id
            }
          }
        }
      }
    }
  }
`;

const GET_TK_PRODUCT_IDS = gql`
  query {
    shop {
      metafield(namespace: "threekit", key: "items") {
        id
        value
      }
    }
  }
`;

const GET_TK_ASSET_ID = gql`
  query($id: ID!) {
    product(id: $id) {
      metafield(namespace: "threekit", key: "assetid") {
        value
        id
      }
    }
  }
`;

const handleToggle = () => !this.state.active ? this.setState({active: true}) : this.setState({active: true});

class ResourceListWithProducts extends React.Component {
  state = {
    ids: [],
    tkId: {},
    field: '',
    active: true,
    setActive: true
  };

  _queryMe = data => {
    if (data.shop.metafield == null) {
      return null;
    } else {
      const str = JSON.parse(data.shop.metafield.value);
      let arr;
      arr = str.ids.split(",");

      this.setState({
        ids: arr.map(id => "gid://shopify/Product/" + id)
      });
    }
  };

  _setTkID = data => {
    this.setState({
      tkId: data
    });
    console.log(data);
  };

  getTkAsset = () => {
    return (
      <Query query={GET_TK_ASSET_ID} onCompleted={data => this._setTkID(data)}>
        {() => {
          console.log("set");
          console.log(this.state);
          return null;
        }}
      </Query>
    );
  };

  handleTKChange = (e) => {
    this.setState({
      field: e
    })
    // console.log(e.target)
  }

  static contextType = Context;
  render() {
    const app = this.context;
    const redirectToProduct = () => {
      const redirect = Redirect.create(app);
      redirect.dispatch(Redirect.Action.APP, "/edit-products");
    };
    

    const TkEditForm = ({ id }) => (
      <Query
        query={GET_TK_ASSET_ID}
        variables={{ id }}
        // skip={!breed}
      >
        {({ loading, error, data, refetch }) => {
          if (loading) return null;
          if (error) return `Error! ${error}`;

          return (
            <div>
              {console.log(data)}
              <Button
            onClick={()=>console.log('toggle')}
            ariaExpanded={this.state.active}
            ariaControls="basic-collapsible"
          >
            Toggle
          </Button>
          <Collapsible
            open={this.state.active}
            id="basic-collapsible"
            transition={{duration: '150ms', timingFunction: 'ease'}}
          >
            <TextContainer>
              Your mailing list lets you contact customers or visitors who have
              shown an interest in your store. Reach out to them with exclusive
              offers or updates about your products.
            </TextContainer>
          </Collapsible>
            {/* Catch the error for no IDS */}
            <TextField value={this.state.field} placeholder={!data.product.metafield ? 'No Threekit ID' : data.product.metafield.value } onChange={this.handleTKChange}></TextField> 
              <button onClick={() => refetch()}>Refetch!</button>
            </div>
          );
        }}
      </Query>
    );

    return (
      <div>
        <Query
          query={GET_TK_PRODUCT_IDS}
          onCompleted={data => this._queryMe(data)}
        >
          {() => {
            return (
              <div>
                {""}
              </div>
            );
          }}
        </Query>{" "}
        {/* this.state.ids */}
        <Query query={GET_PRODUCTS_BY_ID} variables={{ ids: store.get("ids") }}>
          {({ data, loading, error }) => {
            if (loading) return <div>Loading…</div>;
            if (error) return <div>{error.message}</div>;
            console.log(data);
            return (
              <Card sectioned>
                <ResourceList
                  showHeader
                  resourceName={{
                    singular: "Product",
                    plural: "Products"
                  }}
                  items={data.nodes}
                  renderItem={item => {
                    const media = (
                      <Thumbnail
                        source={
                          item.images.edges[0]
                            ? item.images.edges[0].node.originalSrc
                            : ""
                        }
                        alt={
                          item.images.edges[0]
                            ? item.images.edges[0].node.altText
                            : ""
                        }
                      />
                    );
                    const price = item.variants.edges[0].node.price;
                    return (
                      <ResourceList.Item
                        id={item.id}
                        media={media}
                        accessibilityLabel={`View details for ${item.title}`}
                        onClick={() => {
                          console.log(item);
                        }}
                      >
                        <Stack vertical>
                          <Stack.Item fill>
                            <h3>
                              <TextStyle variation="strong">
                                {item.title}
                              </TextStyle>
                            </h3>
                          </Stack.Item>

                          <Stack.Item>
                            <TkEditForm id={item.id} />
                          </Stack.Item>
                          <Stack.Item>
                            <Button>Save</Button>
                          </Stack.Item>
                        </Stack>
                      </ResourceList.Item>
                    );
                  }}
                />
              </Card>
            );
          }}
        </Query>
      </div>
    );
  }
}

export default ResourceListWithProducts;
