import React, { useState, useCallback } from "react";
import { Query, Mutation, useQuery } from "react-apollo";
import gql from "graphql-tag";
import {
  Banner,
  DisplayText,
  Form,
  FormLayout,
  Frame,
  Layout,
  Page,
  PageActions,
  TextField,
  Card,
  Stack,
  TextStyle,
  Checkbox,
  Button
} from "@shopify/polaris";

function ThreekitForm(props) {
  // Declare a new state variable, which we'll call "count"
  const [value, setValue] = useState("");
  const [tkMetaId, setTkMetaId] = useState();

  const UPDATE_TK_ID = gql`
    mutation($input: ProductInput!) {
      productUpdate(input: $input) {
        product {
          metafields(first: 100) {
            edges {
              node {
                id
                namespace
                key
                value
              }
            }
          }
        }
      }
    }
  `;

  const GET_META_ID = gql`
    query($id: ID!) {
      product(id: $id) {
        metafield(namespace: "threekit", key: "assetid") {
          value
          id
        }
      }
    }
  `;

  const handleChange = useCallback(newValue => setValue(newValue), []);
  const setTkMetaField = id => setTkMetaId(id);
  return (
    <div>
      <Query
        query={props.query}
        variables={props.id}
        // skip={!breed}
      >
        {({ loading, error, data, refetch }) => {
          if (loading) return null;
          if (error) return `Error! ${error}`;

          return (
            <div>
              <Query
                query={GET_META_ID}
                variables={props.id}
                // skip={!breed}
                onCompleted={data => setTkMetaId(data)}
              >
                {({ loading, error, data, refetch }) => {
                  if (loading) return null;
                  if (error) return `Error! ${error}`;

                return null
                }}
              </Query>
              {/* <Card title={props.title}> */}
              {/* {console.log(data)} */}

              <Stack>
                <Stack.Item fill>
                  <h3>
                    <TextStyle variation="strong">{props.title}</TextStyle>
                  </h3>
                </Stack.Item>
                <Stack.Item>
                  <Mutation mutation={UPDATE_TK_ID}>
                    {(handleSubmit, { err, res }) => {
                      const showError = err && (
                        <Banner status="critical">{err.message}</Banner>
                      );
                      const showToast = res && res.productVariantUpdate && (
                        <Toast
                          content="Sucessfully updated"
                          onDismiss={() => console.log("UPDATED")}
                        />
                      );

                      return (
                        <div>
                          {/* <Frame> */}
                          {/* <Page> */}
                          {/* <Layout> */}
                          {showToast}
                          {/* <Layout.Section>{showError}</Layout.Section> */}
                          {/* <Layout.Section> */}

                          <Form>
                            <FormLayout>
                              <FormLayout.Group>
                                <TextField
                                  value={value}
                                  onChange={handleChange}
                                  // onFocus={getID(!data.product.metafield ? "No Threekit ID" : data.product.metafield.value)}
                                  id={data.product.id}
                                  ariaControls={data.product.id}
                                  placeholder={
                                    !data.product.metafield
                                      ? "No Threekit ID"
                                      : data.product.metafield.value
                                  }
                                  connectedRight={
                                    <Button
                                      onClick={() => {
                                        let productVariableInput;
                                        if (!data.product.metafield) {
                                          productVariableInput = {
                                            id: props.id.id,
                                            metafields: [
                                              {
                                                namespace: "threekit",
                                                key: "assetid",
                                                value: value,
                                                valueType: "STRING"
                                              }
                                            ]
                                          };
                                        } else {
                                          productVariableInput = {
                                            id: props.id.id,
                                            metafields: [
                                              {
                                                namespace: "threekit",
                                                key: "assetid",
                                                value: value,
                                                valueType: "STRING",
                                                id: tkMetaId.product.metafield.id
                                              }
                                            ]
                                          };
                                        }

                                        handleSubmit({
                                          variables: {
                                            input: productVariableInput
                                          }
                                        });
                                      }}
                                    >
                                      Save
                                    </Button>
                                  }
                                ></TextField>
                              </FormLayout.Group>
                            </FormLayout>

                            <PageActions
                              primaryAction={[
                                {
                                  content: "Save",
                                  onAction: () => {
                                    let productVariableInput;
                                    if (!data.product.metafield) {
                                      productVariableInput = {
                                        id: props.id.id,
                                        metafields: [
                                          {
                                            namespace: "threekit",
                                            key: "assetid",
                                            value: value,
                                            valueType: "STRING"
                                          }
                                        ]
                                      };
                                    } else {
                                      productVariableInput = {
                                        id: props.id.id,
                                        metafields: [
                                          {
                                            namespace: "threekit",
                                            key: "assetid",
                                            value: value,
                                            valueType: "STRING",
                                            id: tkMetaId.product.metafield.id
                                          }
                                        ]
                                      };
                                    }
                                    console.log(productVariableInput);

                                    // handleSubmit({
                                    //   variables: {
                                    //     input: productVariableInput
                                    //   }
                                    // });
                                  }
                                }
                              ]}
                            />
                          </Form>
                          {/* </Layout.Section> */}
                          {/* </Layout> */}
                          {/* </Page> */}
                          {/* </Frame> */}
                        </div>
                      );
                    }}
                  </Mutation>
                </Stack.Item>
              </Stack>
              {/* Catch the error for no IDS */}

              {/* <button onClick={() => refetch()}>Refetch!</button> */}
              {/* </Card> */}
            </div>
          );
        }}
      </Query>
    </div>
  );
}

export default ThreekitForm;
