import gql from "graphql-tag";
import { Query, ApolloConsumer } from "react-apollo";
import {
  Card,
  ResourceList,
  Stack,
  TextStyle,
  Thumbnail,
  TextField,
  Button
} from "@shopify/polaris";
import store from "store-js";
import { Redirect } from "@shopify/app-bridge/actions";
import { Context } from "@shopify/app-bridge-react";

const GET_PRODUCTS_BY_ID = gql`
  query getProducts($ids: [ID!]!) {
    nodes(ids: $ids) {
      ... on Product {
        title
        handle
        descriptionHtml
        id
        images(first: 1) {
          edges {
            node {
              originalSrc
              altText
            }
          }
        }
        variants(first: 1) {
          edges {
            node {
              price
              id
            }
          }
        }
      }
    }
  }
`;

const GET_TK_PRODUCT_IDS = gql`
  query {
    shop {
      metafield(namespace: "threekit", key: "items") {
        id
        value
      }
    }
  }
`;

const GET_TK_ASSET_ID = gql`
  query($id: ID!) {
    product(id: $id) {
      metafield(namespace: "threekit", key: "assetid") {
        value
        id
      }
    }
  }
`;

class ResourceListWithProducts extends React.Component {
  state = {
    ids: [],
    tkId: {}
  };

  _queryMe = data => {
    if (data.shop.metafield == null) {
      return null;
    } else {
      const str = JSON.parse(data.shop.metafield.value);
      let arr;
      arr = str.ids.split(",");

      this.setState({
        ids: arr.map(id => "gid://shopify/Product/" + id)
      });
    }
  };

  _setTkID = data => {
      this.setState({
        tkId: data
      });
    console.log(data)
  };

  getTkAsset = () => {
    return (
        <Query
          query={GET_TK_ASSET_ID}
          onCompleted={data => this._setTkID(data)}
        >
          {() => {
            console.log('set')
            console.log(this.state)
            return null;
          }}
        </Query>
     
    );
  };

  static contextType = Context;
  render() {
    const app = this.context;
    const redirectToProduct = () => {
      const redirect = Redirect.create(app);
      redirect.dispatch(Redirect.Action.APP, "/edit-products");
    };
    const twoWeeksFromNow = new Date(Date.now() + 12096e5).toDateString();



    return (
      <div>
        <Query
          query={GET_TK_PRODUCT_IDS}
          onCompleted={data => this._queryMe(data)}
        >
          {() => {
            return (
              <div>
                <p>Resource list</p>
              </div>
            );
          }}
        </Query>{" "}
        {/* this.state.ids */}
        <Query query={GET_PRODUCTS_BY_ID} variables={{ ids: store.get("ids") }}>
          {({ data, loading, error }) => {
            if (loading) return <div>Loading…</div>;
            if (error) return <div>{error.message}</div>;
            console.log(data);
            return (
              <Card>
                <ResourceList
                  showHeader
                  resourceName={{
                    singular: "Product",
                    plural: "Products"
                  }}
                  items={data.nodes}
                  renderItem={item => {
                    const media = (
                      <Thumbnail
                        source={
                          item.images.edges[0]
                            ? item.images.edges[0].node.originalSrc
                            : ""
                        }
                        alt={
                          item.images.edges[0]
                            ? item.images.edges[0].node.altText
                            : ""
                        }
                      />
                    );
                    const price = item.variants.edges[0].node.price;
                    return (
                      <ResourceList.Item
                        id={item.id}
                        media={media}
                        accessibilityLabel={`View details for ${item.title}`}
                        onClick={() => {
                          this.getTkAsset();
                          // console.log(item);
                        }}
                      >
                        <Stack>
                          <Stack.Item fill>
                            <h3>
                              <TextStyle variation="strong">
                                {item.title}
                              </TextStyle>
                            </h3>
                          </Stack.Item>
                          <Stack.Item>
                            <TextField label="TK ID" value={""} />
                            <Button>Load TK ID</Button>

                            <Button>Save</Button>
                          </Stack.Item>
                          <Stack.Item></Stack.Item>
                        </Stack>
                      </ResourceList.Item>
                    );
                  }}
                />
              </Card>
            );
          }}
        </Query>
      </div>
    );
  }
}

export default ResourceListWithProducts;
