import { Layout, Page, TextStyle, EmptyState, Button } from "@shopify/polaris";
import { ResourcePicker, TitleBar } from "@shopify/app-bridge-react";
import SnippetGen from "../components/SnippetGen";

class SnippetGenerator extends React.Component {
  state = {
    open: false,
  };

  componentDidMount() {}

  render() {
    return (
      <Page>
        <div>
        <ResourcePicker
          resourceType="Product"
          showVariants={false}
          open={this.state.open}
          onSelection={resources => this.handleSelection(resources)}
          onCancel={() => this.setState({ open: false })}
        />
          <SnippetGen />
        </div>
      </Page>
    );
  }
  handleSelection = resources => {
    const idsFromResources = resources.selection.map(product => product.id);
    this.setState({ open: false });
    console.log(idsFromResources)
    console.log(this._formatProductID(idsFromResources))
    // this.makeShopMetafield(idsFromResources.map(x => x.split('Product/')[1]))
    store.set("ids", idsFromResources);
  };
}
export default SnippetGenerator;
