import {
  Banner,
  Card,
  DisplayText,
  Form,
  FormLayout,
  Frame,
  Layout,
  Page,
  PageActions,
  TextField,
  Toast
} from "@shopify/polaris";
import store from "store-js";
import gql from "graphql-tag";
import { Mutation, Query, useQuery } from "react-apollo";

let metaId = "";
let tktempid = "";

const UPDATE_PRICE = gql`
  mutation($input: ProductInput!) {
    productUpdate(input: $input) {
      product {
        metafields(first: 100) {
          edges {
            node {
              id
              namespace
              key
              value
            }
          }
        }
      }
    }
  }
`;

const GET_META_ID = gql`
query($id: ID!) {
  product(id: $id) {
    metafield(namespace: "threekit", key: "assetid") {
      value
      id
    }
  }
}
`;

function getMetaValue(proId) {
  const { loading, error, data } = useQuery(GET_META_ID, {
    variables: {id: proId}
  });

  if (loading) return null;
  if (error) return `Error! ${error}`;
  console.log(data)
  if(data.product.metafield == null) {return null} else{
    this.setState({tkid: data.product.metafield.value}) 
  };
}

class EditProduct extends React.Component {
  state = {
    tkid: "",
    price: "",
    variantId: "",
    showToast: false,
    metaId: ""
  };

  componentDidMount() {
    this.setState({ tkid: this.itemToBeConsumed() });
    
  }
  

  render() {
    const { name, price, tkid, variantId } = this.state;


    function getMeta(proId) {
      const { loading, error, data } = useQuery(GET_META_ID, {
        variables: {id: proId}
      });

      if (loading) return null;
      if (error) return `Error! ${error}`;
      console.log(data)
      if(data.product.metafield == null) {return null} else{
        metaId = data.product.metafield.id;
        tktempid = data.product.metafield.value;
        return data;
      };
    }

 


    return (
      
      <Mutation mutation={UPDATE_PRICE}>
        {(handleSubmit, { error, data }) => {
          const showError = error && (
            <Banner status="critical">{error.message}</Banner>
          );
          const showToast = data && data.productVariantUpdate && (
            <Toast
              content="Sucessfully updated"
              onDismiss={() => this.setState({ showToast: false })}
            />
          );


          return (
            <Frame>
              <Page>
                <Layout>
                  {showToast}
                  <Layout.Section>{showError}</Layout.Section>
                  <Layout.Section>
                    <DisplayText size="large">{name}</DisplayText>
                    <Form>
                      <Card sectioned>
                        <p>{JSON.stringify(getMeta(this.state.variantId))}</p>
                        <FormLayout>
                          <FormLayout.Group>
                            <TextField
                              prefix="Threekit Asset ID"
                              value={tkid}
                              onChange={this.handleChange("tkid")}
                              label="Enter your Threekit Asset ID"
                              type="text"
                            />
                          </FormLayout.Group>
                          <p>This sale price will expire in two weeks</p>
                        </FormLayout>
                      </Card>
                      <PageActions
                        primaryAction={[
                          {
                            content: "Save",
                            onAction: () => {
                              let productVariableInput;
                              if (metaId == ""){
                                productVariableInput = {
                                  id: this.state.variantId,
                                  metafields: [
                                    {
                                      namespace: "threekit",
                                      key: "assetid",
                                      value: this.state.tkid,
                                      valueType: "STRING"
                                    }
                                  ]
                                };
                              } else {
                                productVariableInput = {
                                  id: this.state.variantId,
                                  metafields: [
                                    {
                                      namespace: "threekit",
                                      key: "assetid",
                                      value: this.state.tkid,
                                      valueType: "STRING",
                                      id: metaId
                                    }
                                  ]
                                };
                              }
                              
                              handleSubmit({
                                variables: { input: productVariableInput }
                              });
                            }
                          }
                        ]}
                        secondaryActions={[
                          {
                            content: "Remove discount"
                          }
                        ]}
                      />
                    </Form>
                  </Layout.Section>
                </Layout>
              </Page>
            </Frame>
          );
        }}
      </Mutation>
    );
  }

  handleChange = field => {
    return value => this.setState({ [field]: value });
  };

  itemToBeConsumed = () => {
    const item = store.get("item");
    console.log(item);

    const price = item.handle;
    const variantId = item.id;
    const discounter = price;
    this.setState({ price, variantId });
    return discounter;
  };
}

export default EditProduct;
