import gql from "graphql-tag";
import { Query } from "react-apollo";
import {
  Card,
  ResourceList,
  Stack,
  TextStyle,
  Thumbnail
} from "@shopify/polaris";
import store from "store-js";
import { Redirect } from "@shopify/app-bridge/actions";
import { Context } from "@shopify/app-bridge-react";

const GET_PRODUCTS_BY_ID = gql`
  query getProducts($ids: [ID!]!) {
    nodes(ids: $ids) {
      ... on Product {
        title
        handle
        descriptionHtml
        id
        images(first: 1) {
          edges {
            node {
              originalSrc
              altText
            }
          }
        }
        variants(first: 1) {
          edges {
            node {
              price
              id
            }
          }
        }
      }
    }
  }
`;

const GET_TK_PRODUCT_IDS = gql`
  query {
    shop {
      metafield(namespace: "threekit", key: "items") {
        id
        value
      }
    }
  }
`;

class ResourceListWithProducts extends React.Component {
  state = {
    ids: []
  };

  _queryMe = data => {
    if (data.shop.metafield == null) {
      return null;
    } else {
      this.setState({
        ids: JSON.parse(data.shop.metafield.value)
      });
    }
  };

  makeIds = arr => arr.map(id => "gid://shopify/Product/" + id)

  // _formatIDs = (src) => {
  //   const ids = src.split(',');
  //   ids.map(id => "gid://shopify/Product/" + id);
  // }

  static contextType = Context;
  render() {
    const app = this.context;
    const redirectToProduct = () => {
      const redirect = Redirect.create(app);
      redirect.dispatch(Redirect.Action.APP, "/edit-products");
    };
    const twoWeeksFromNow = new Date(Date.now() + 12096e5).toDateString();

    //Test
    let idString = "";
    let idArr = "";

    return (
      <div>
        <Query
          query={GET_TK_PRODUCT_IDS}
          onCompleted={data => this._queryMe(data)}
        >
          {() => {
            return (
              <div>
                <p>NEW STATE</p>
                <p>{JSON.stringify(this.state)}</p>
                {/* {console.log(this.state['ids']['ids'])} */}
                {/* {console.log(typeof(this.state['ids']['ids']))} */}
                {console.log(Object.values(this.state))}

                <Query query={GET_PRODUCTS_BY_ID} variables={{ ids: store.get("ids") }}>
          {({ data, loading, error }) => {
            if (loading) return <div>Loadingâ€¦</div>;
            if (error) return <div>{error.message}</div>;
            console.log(data);
            return (
              <Card>
                <ResourceList
                  showHeader
                  resourceName={{ singular: "Product", plural: "Products" }}
                  items={data.nodes}
                  renderItem={item => {
                    const media = (
                      <Thumbnail
                        source={
                          item.images.edges[0]
                            ? item.images.edges[0].node.originalSrc
                            : ""
                        }
                        alt={
                          item.images.edges[0]
                            ? item.images.edges[0].node.altText
                            : ""
                        }
                      />
                    );
                    const price = item.variants.edges[0].node.price;
                    return (
                      <ResourceList.Item
                        id={item.id}
                        media={media}
                        accessibilityLabel={`View details for ${item.title}`}
                        onClick={() => {
                          store.set("item", item);
                          redirectToProduct();
                        }}
                      >
                        <Stack>
                          <Stack.Item fill>
                            <h3>
                              <TextStyle variation="strong">
                                {item.title}
                              </TextStyle>
                            </h3>
                          </Stack.Item>
                          <Stack.Item>
                            <p>${price}</p>
                          </Stack.Item>
                          <Stack.Item>
                            <p>Expires on {twoWeeksFromNow} </p>
                          </Stack.Item>
                        </Stack>
                      </ResourceList.Item>
                    );
                  }}
                />
              </Card>
            );
          }}
        </Query>
              </div>
            );
          }}
        </Query>

       
      </div>
    );
  }
}

export default ResourceListWithProducts;
