import React, {useState} from "react";
import {
  SkeletonPage,
  TextContainer,
  TextField,
  Button,
  Modal
} from "@shopify/polaris";

const [value, setValue] = useState('1776 Barnes Street\nOrlando, FL 32801');

function SnippetGen(props) {
  return (
    <div>
      <Modal
        open={props.active}
        onClose={props.handleChange}
        title={props.title}
        primaryAction={{
          content: "Add Instagram",
          onAction: props.handleChange
        }}
      >
        <Modal.Section>
          <TextContainer>
            <TextField multiline value={value} />
          </TextContainer>
        </Modal.Section>
      </Modal>
    </div>
  );
}

export default SnippetGen;
