import React, { useState, useCallback, useEffect } from "react";
import { Query, Mutation } from "react-apollo";
import gql from "graphql-tag";
import { TextField, Button } from "@shopify/polaris";

function TokenForm(props) {
  const [tkToken, setTkToken] = useState('No Threekit ID');

  const GET_TK_TOKEN = gql`
    query {
      shop {
        metafield(namespace: "threekit", key: "token") {
          id
          value
        }
      }
    }
  `;

  const handleChange = useCallback(newValue => setTkToken(newValue), []);
  return (
    <div>
      <Query
        query={GET_TK_TOKEN}
        // variables={props.id}
        onCompleted={data => setTkToken(data)}
      >
        {({ loading, error, data, refetch }) => {
          if (loading) return null;
          if (error) return `Error! ${error}`;
          console.log("data");
          console.log(data);
          console.log(tkToken);

          return (
            <TextField
              label={"Threekit Token"}
              value={!data.shop.metafield? tkToken : JSON.stringify(tkToken)}
              onChange={handleChange}
              // id={data.product.id}
              // ariaControls={data.product.id}
              placeholder={JSON.stringify(tkToken.shop)}
              connectedRight={
                <div>
                  <Button primary onClick={() => console.log("Clicked")}>
                    Save
                  </Button>
                </div>
              }
            ></TextField>
          );
        }}
      </Query>
    </div>
  );
}

export default TokenForm;
