import React, { useState, useCallback, useEffect } from "react";
import { Query, Mutation } from "react-apollo";
import gql from "graphql-tag";
import {
  Banner,
  DisplayText,
  Form,
  FormLayout,
  Frame,
  Layout,
  Page,
  PageActions,
  TextField,
  Card,
  Stack,
  TextStyle,
  Checkbox,
  Button,
  Toast
} from "@shopify/polaris";

function ThreekitForm(props) {
  // Declare a new state variable, which we'll call "count"
  const [value, setValue] = useState("");
  const [tkMetaId, setTkMetaId] = useState();
  const [isSaved, setIsSaved] = useState(false);

  const [isPdp, setIsPdp] = useState({});
  const [isChecked, setIsChecked] = useState(false);

  const [isAr, setIsAr] = useState({});
  const [arChecked, setArChecked] = useState(false);

  const UPDATE_TK_ID = gql`
    mutation($input: ProductInput!) {
      productUpdate(input: $input) {
        product {
          metafields(first: 100) {
            edges {
              node {
                id
                namespace
                key
                value
              }
            }
          }
        }
      }
    }
  `;

  const GET_META_ID = gql`
    query($id: ID!) {
      product(id: $id) {
        metafield(namespace: "threekit", key: "assetid") {
          value
          id
        }
      }
    }
  `;

  const IS_PLAYER_PDP = gql`
    query($id: ID!) {
      product(id: $id) {
        metafield(namespace: "threekit", key: "pdp") {
          value
          id
        }
      }
    }
  `;

  const IS_AR_ENABLED = gql`
    query($id: ID!) {
      product(id: $id) {
        metafield(namespace: "threekit", key: "ARen") {
          value
          id
        }
      }
    }
  `;

  const UPDATE_TK_ATTR = gql`
    mutation($input: ProductInput!) {
      productUpdate(input: $input) {
        product {
          metafields(first: 100) {
            edges {
              node {
                id
                namespace
                key
                value
              }
            }
          }
        }
      }
    }
  `;

  const handleChange = useCallback(newValue => setValue(newValue), []);

  const setCheck = val => setIsChecked(val);
  const setArCheck = val => setArChecked(val);

  const _createTKimage = id => {
    const formatId = id => id.split("Product/")[1];

    const getImgObj = id => {
      var fetchUrl = "/api/images/" + formatId(id);
      var method = "GET";
      fetch(fetchUrl, { method: method })
        .then(response => response.json())
        .then(json => {
          // console.log(json.data.metafields)
          // this.setState({tkMetaId: json.data})
          console.log(json);
          // setProdImg(json.data);
        });
    };
    getImgObj(id);
    // isPdp ? console.log("Yes this is a PDP tk item") : console.log("No this is not a PDP item");
  };

  const updateMeta = (data, bool, namespace, key, obj) => {
    let productVariableInput;
    if (!data.product.metafield) {
      bool
        ? (productVariableInput = {
            id: props.id.id,
            metafields: [
              {
                namespace: namespace,
                key: key,
                value: 'false',
                valueType: "STRING"
              }
            ]
          })
        : (productVariableInput = {
            id: props.id.id,
            metafields: [
              {
                namespace: namespace,
                key: key,
                value: 'true',
                valueType: "STRING"
              }
            ]
          });
    } else {
      bool
        ? (productVariableInput = {
            id: props.id.id,
            metafields: [
              {
                namespace: namespace,
                key: key,
                value: 'false',
                valueType: "STRING",
                id: obj.product.metafield.id
              }
            ]
          })
        : (productVariableInput = {
            id: props.id.id,
            metafields: [
              {
                namespace: namespace,
                key: key,
                value: 'true',
                valueType: "STRING",
                id: obj.product.metafield.id
              }
            ]
          });
    }

    handleSubmit({
      variables: {
        input: productVariableInput
      }
    });
  };
  return (
    <div>
      <Stack>
        <Query query={props.query} variables={props.id}>
          {({ loading, error, data, refetch }) => {
            if (loading) return null;
            if (error) return `Error! ${error}`;

            return (
              <div>
                <Query
                  query={GET_META_ID}
                  variables={props.id}
                  onCompleted={data => setTkMetaId(data)}
                >
                  {({ loading, error, data, refetch }) => {
                    if (loading) return null;
                    if (error) return `Error! ${error}`;

                    return null;
                  }}
                </Query>

                <Stack.Item fill>
                  <h3>
                    <TextStyle variation="strong">{props.title}</TextStyle>
                  </h3>
                </Stack.Item>

                <Stack.Item>
                  <Mutation
                    mutation={UPDATE_TK_ID}
                    onCompleted={() => setIsSaved(true)}
                  >
                    {(handleSubmit, { err, res }) => {
                      const showError = err && (
                        <Banner status="critical">{err.message}</Banner>
                      );

                      return (
                        <div>
                          {/* <Frame> */}
                          <Page>
                            {/* <Layout> */}
                            {!isSaved ? (
                              <div></div>
                            ) : (
                              <Toast
                                content="Saved"
                                onDismiss={() => setIsSaved(false)}
                              ></Toast>
                            )}

                            <Layout.Section>{showError}</Layout.Section>
                            {/* <Layout.Section> */}

                            <Form>
                              <FormLayout>
                                <FormLayout.Group>
                                  <TextField
                                    value={value}
                                    onFocus={() =>
                                      setValue(
                                        !data.product.metafield
                                          ? "No Threekit ID"
                                          : data.product.metafield.value
                                      )
                                    }
                                    onChange={handleChange}
                                    id={data.product.id}
                                    ariaControls={data.product.id}
                                    placeholder={
                                      !data.product.metafield
                                        ? "No Threekit ID"
                                        : data.product.metafield.value
                                    }
                                    connectedRight={
                                      <Button
                                        onClick={() => {
                                          let productVariableInput;
                                          if (!data.product.metafield) {
                                            productVariableInput = {
                                              id: props.id.id,
                                              metafields: [
                                                {
                                                  namespace: "threekit",
                                                  key: "assetid",
                                                  value: value,
                                                  valueType: "STRING"
                                                }
                                              ]
                                            };
                                          } else {
                                            productVariableInput = {
                                              id: props.id.id,
                                              metafields: [
                                                {
                                                  namespace: "threekit",
                                                  key: "assetid",
                                                  value: value,
                                                  valueType: "STRING",
                                                  id:
                                                    tkMetaId.product.metafield
                                                      .id
                                                }
                                              ]
                                            };
                                          }

                                          handleSubmit(
                                            {
                                              variables: {
                                                input: productVariableInput
                                              }
                                            }

                                            // _createTKimage(props.id.id)
                                          );
                                        }}
                                      >
                                        Save
                                      </Button>
                                    }
                                  ></TextField>
                                </FormLayout.Group>
                              </FormLayout>
                            </Form>
                          </Page>
                          {/* </Frame> */}
                        </div>
                      );
                    }}
                  </Mutation>
                </Stack.Item>
              </div>
            );
          }}
        </Query>
        <Stack.Item>
          <Query
            query={IS_PLAYER_PDP}
            variables={props.id}
            onCompleted={data => setIsPdp(data)}
          >
            {({ loading, error, data, refetch }) => {
              if (loading) return null;
              if (error) return `Error! ${error}`;
              console.log(data);
              return (
                <Mutation
                  mutation={UPDATE_TK_ATTR}
                  onCompleted={() => setIsSaved(true)}
                >
                  {(handleSubmit, { err, res }) => {
                    const showError = err && (
                      <Banner status="critical">{err.message}</Banner>
                    );
                    if (
                      !data.product.metafield ||
                      data.product.metafield.value == "false"
                    ) {
                      useEffect(() => {
                        setIsChecked(false);
                      }, [setIsChecked]);
                    } else {
                      useEffect(() => {
                        setIsChecked(true);
                      }, [setIsChecked]);
                    }

                    return (
                      <div>
                        <Checkbox
                          label="Display on PDP"
                          checked={isChecked}
                          onChange={() => updateMeta(data, isChecked, 'threekit', 'pdp', isPdp)}
                          // onChange={() => {
                          //   let productVariableInput;
                          //   if (!data.product.metafield) {
                          //     isChecked
                          //       ? (productVariableInput = {
                          //           id: props.id.id,
                          //           metafields: [
                          //             {
                          //               namespace: "threekit",
                          //               key: "pdp",
                          //               value: "false",
                          //               valueType: "STRING"
                          //             }
                          //           ]
                          //         })
                          //       : (productVariableInput = {
                          //           id: props.id.id,
                          //           metafields: [
                          //             {
                          //               namespace: "threekit",
                          //               key: "pdp",
                          //               value: "true",
                          //               valueType: "STRING"
                          //             }
                          //           ]
                          //         });
                          //   } else {
                          //     isChecked
                          //       ? (productVariableInput = {
                          //           id: props.id.id,
                          //           metafields: [
                          //             {
                          //               namespace: "threekit",
                          //               key: "pdp",
                          //               value: "false",
                          //               valueType: "STRING",
                          //               id: isPdp.product.metafield.id
                          //             }
                          //           ]
                          //         })
                          //       : (productVariableInput = {
                          //           id: props.id.id,
                          //           metafields: [
                          //             {
                          //               namespace: "threekit",
                          //               key: "pdp",
                          //               value: "true",
                          //               valueType: "STRING",
                          //               id: isPdp.product.metafield.id
                          //             }
                          //           ]
                          //         });
                          //   }

                          //   handleSubmit({
                          //     variables: {
                          //       input: productVariableInput
                          //     }
                          //   });
                          // }}
                          //
                          onFocus={() =>
                            isChecked ? setCheck(false) : setCheck(true)
                          }
                        />
                      </div>
                    );
                  }}
                </Mutation>
              );
            }}
          </Query>
        </Stack.Item>
      </Stack>
    </div>
  );
}

export default ThreekitForm;
